const submissionsContainer = document.getElementById("submissions-container");

function renderSubmissions(submissionsResponse) {
    const submissions = submissionsResponse.results;
    submissions
        .sort((a, b) => b.speakers.length - a.speakers.length)
        .forEach((submission) => {
        if (submission.state !== "confirmed") return;
        const submissionElement = document.createElement("div");
        submissionElement.className = "submission";

        const speakers = submission.speakers.map((speaker) => `
            <div class="speaker">
                ${speaker.avatar ?
                `<img src="${speaker.avatar || ''}" alt="${speaker.avatar || ''}" class="speaker-avatar" />` : ''
            }
                <h4>${speaker.name}</h4>
                <p>${speaker.biography || ''}</p>
            </div>
        `).join("");

        submissionElement.innerHTML = `
            <h3 class="submission-title">${submission.title}</h3>
            <p class="submission-abstract">${submission.abstract}</p>
            <div class="submission-speakers">
                ${speakers}
            </div>
            <p class="submission-track"><strong>Nivel:</strong> ${submission.track["pt-br"]}</p>
            <p class="submission-type"><strong>Tipo:</strong> ${submission.submission_type["pt-br"]}</p>
            <p class="submission-duration"><strong>Dura√ß√£o:</strong> ${submission.duration} minutos</p>
        `;

        submissionsContainer.appendChild(submissionElement);
    });
}

// Call the function to render submissions
renderSubmissions(submissionsResponse);